INSERT INTO `OAy9d5UrW_posts` VALUES("2418", "1", "2017-01-09 12:30:13", "2017-01-09 12:30:13", "חברת המניע משאבות בע\"מ אשר נוסדה בשנת 1937,\r\nהפכה מאז לאחת היצרניות הבודדות הפועלות כיום בתחום המשאבות הצנטריפוגליות בארץ.\r\n\r\nלחברה יש בית מלאכה רחב ידיים במפעלה שבתל-אביב ובו היא בין היתר מבצעת חריטת מאיצים למידה הרצויה ,\r\n\r\nאיזון דינאמי של מאיצים, ייצור חלקי משאבות כצירים תותבים ואטמים, ייצור בסיסים ומסגרות למשאבות, הרכבת חלקי המשאבה\r\n\r\nצביעה ובדיקות לחץ לפני אספקה.\r\n\r\nהמניע משאבות חברה בהתאחדות התעשיינים ומחזיקה בתקן ISO המחמיר ליצרנים.\r\n\r\nהחברה מעסיקה אנשי מקצוע בעלי ניסיון של עשרות שנים בייצור ותיקון משאבות ומתגאה להיות גורם חשוב בתעשיית הייצור כחול לבן הישראלית.\r\n\r\nהידע הרב והמומחיות שצברה החברה במהלך שנותיה הרבות ויכולות הייצור הקיימות אצלה מאפשרים מתן מענה לאתגרים רבים בתחום המשאבות\r\n\r\nבשימת דגש על איכות, מקצועיות והכול תוך גילוי גמישות וחדשנות ביצועית.\r\n<p dir=\"rtl\"></p>", "יצור מקומי-כחול לבן", "", "publish", "closed", "closed", "", "%d7%99%d7%a6%d7%95%d7%a8", "", "", "2017-02-23 11:02:23", "2017-02-23 11:02:23", "", "2333", "http://codenroll.pro/hamenia/?page_id=2418", "0", "page", "", "0");
