INSERT INTO `OAy9d5UrW_posts` VALUES("2483", "1", "2017-02-09 16:20:14", "2017-02-09 16:20:14", "כחברה הנמנית עם החברות המובילות בתחום המשאבות בישראל וכאחת החלוצות בתחום בארץ , אוגרת בחובה החברה ידע טכני רב ומומחיות שהיא שמחה להעביר ללקוחותיה במסגרת הדרכות מרוכזות שהיא מבצעת מעת לעת למהנדסים, אנשי תחזוקה, מפעילי משאבות וכל אדם אחר הבא במגע עם משאבות במסגרת עבודתו.\r\n\r\nההדרכות מבוצעות ע\"י אנשי התמיכה הטכנית של החברה במפעלה שבתל-אביב בו ניתן לבצע גם סיור לימודי מעניין וליצור מגע ישיר עם חלקי משאבות וללמוד על תהליך יצורן.\r\n\r\nבמידת הצורך ולפי בקשת הלקוח ניתן לבצע ההדרכה במפעל הלקוח תוך ריכוז כמות סבירה של חניכים, במסגרת הדרכה בבית הלקוח מובאים לתצוגה מספר אלמנטים להמחשת נושא הלימוד.\r\n\r\nהחברה משתתפת ועורכת מעת לעת כנסים מקצועיים ללקוחותיה במגזרים השונים , מיזוג אוויר, שפכים, תעשייה וכו\'. במסגרת כנסים אלה מועברות הרצאות מקצועיות ע\"י נציגי החברה ונציגים נוספים רלבנטיים שמוזמנים על ידע כגון ספקי מנועים, מכון התקנים וכו\'.", "הדרכות", "", "publish", "closed", "closed", "", "%d7%94%d7%93%d7%a8%d7%9b%d7%95%d7%aa", "", "", "2017-02-23 10:58:12", "2017-02-23 10:58:12", "", "2333", "http://codenroll.pro/hamenia/?page_id=2483", "0", "page", "", "0");
