INSERT INTO `OAy9d5UrW_posts` VALUES("2652", "1", "2017-02-27 13:57:05", "2017-02-27 13:57:05", "<label> השם שלך (חובה)\r\n    [text* your-name] </label>\r\n\r\n<label> האימייל שלך (חובה)\r\n    [email* your-email] </label>\r\n\r\n<label> הטלפון שלך (חובה)\r\n    [tel* your-tel] </label>\r\n\r\n<label> נושא\r\n    [text your-subject] </label>\r\n\r\n<label> ההודעה שלך\r\n    [textarea your-message] </label>\r\n\r\n[submit \"שלח\"]\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\nההודעה נשלחה.\nארעה שגיאה בשליחת ההודעה.\nקיימת שגיאה בשדה אחד או יותר.\nארעה שגיאה בשליחת ההודעה.\nעליך להסכים לתנאים לפני שליחת ההודעה.\nזהו שדה חובה.\nהשדה ארוך מדי.\nהשדה קצר מדי.\nשדה התאריך אינו נכון.\nהתאריך מוקדם מהתאריך המותר.\nהתאריך מאוחר מהתאריך המותר.\nשגיאה לא ידועה בהעלאת הקובץ.\nאין לך הרשאות להעלות קבצים בפורמט זה.\nהקובץ גדול מדי.\nשגיאה בהעלאת הקובץ.\nפורמט המספר אינו תקין.\nהמספר קטן מהמינימום המותר.\nהמספר גדול מהמקסימום המותר.\nהתשובה לשאלת הביטחון אינה נכונה.\nהקוד שהזנת שגוי.\nכתובת האימייל שהוזנה אינה תקינה.\nהקישור אינו תקין.\nמספר הטלפון אינו תקין.", "טופס יצירת קשר 1", "", "publish", "closed", "closed", "", "%d7%98%d7%95%d7%a4%d7%a1-%d7%99%d7%a6%d7%99%d7%a8%d7%aa-%d7%a7%d7%a9%d7%a8-1", "", "", "2017-04-19 18:43:06", "2017-04-19 18:43:06", "", "0", "http://codenroll.pro/hamenia/?post_type=wpcf7_contact_form&#038;p=2652", "0", "wpcf7_contact_form", "", "0");
